            minHeight: "100vh",
            display: "flex",
            flexDirection: "column",
            alignContent: "center",
            justifyContent: "center",
            textAlign: "center",
            color: "#fff",
            marginTop: "10vh",
            backgroundImage: 'url(' + imgUrl + ')',
            backgroundSize: 'cover',
            // backgroundColor: 'rgba(128, 128, 128, 0.3)',
            overflow: 'hidden'
        },
        overlaySection: {
            minHeight: "100vh",
            paddingTop: "40vh",
            backgroundColor: "#000000",
            opacity: 0.7,
        },
        a: {
            margin: "0 5px 0 5px"
        },
        downArrow: {
            width: "50px",
            marginTop: "15vh"
        },
        linkSpan: {
            marginTop: "20px"
        },
        img: {
            width: "80px"
        }
};

const HeaderSection = props => (
        <div id="headerSection" style={style.headerSection}>
